type discountThenLoader[Q orm.Loadable] struct {
	OrderItems func(...bob.Mod[*dialect.SelectQuery]) orm.Loader[Q]
	Orders     func(...bob.Mod[*dialect.SelectQuery]) orm.Loader[Q]
}

func buildDiscountThenLoader[Q orm.Loadable]() discountThenLoader[Q] {
	type OrderItemsLoadInterface interface {
		LoadOrderItems(context.Context, bob.Executor, ...bob.Mod[*dialect.SelectQuery]) error
	}
	type OrdersLoadInterface interface {
		LoadOrders(context.Context, bob.Executor, ...bob.Mod[*dialect.SelectQuery]) error
	}

	return discountThenLoader[Q]{
		OrderItems: thenLoadBuilder[Q](
			"OrderItems",
			func(ctx context.Context, exec bob.Executor, retrieved OrderItemsLoadInterface, mods ...bob.Mod[*dialect.SelectQuery]) error {
				return retrieved.LoadOrderItems(ctx, exec, mods...)
			},
		),
		Orders: thenLoadBuilder[Q](
			"Orders",
			func(ctx context.Context, exec bob.Executor, retrieved OrdersLoadInterface, mods ...bob.Mod[*dialect.SelectQuery]) error {
				return retrieved.LoadOrders(ctx, exec, mods...)
			},
		),
	}
}

// LoadOrderItems loads the discount's OrderItems into the .R struct
func (o *Discount) LoadOrderItems(ctx context.Context, exec bob.Executor, mods ...bob.Mod[*dialect.SelectQuery]) error {
	if o == nil {
		return nil
	}

	// Reset the relationship
	o.R.OrderItems = nil

	related, err := o.OrderItems(mods...).All(ctx, exec)
	if err != nil {
		return err
	}

	for _, rel := range related {
		rel.R.Discount = o
	}

	o.R.OrderItems = related
	return nil
}

// LoadOrderItems loads the discount's OrderItems into the .R struct
func (os DiscountSlice) LoadOrderItems(ctx context.Context, exec bob.Executor, mods ...bob.Mod[*dialect.SelectQuery]) error {
	if len(os) == 0 {
		return nil
	}

	orderItems, err := os.OrderItems(mods...).All(ctx, exec)
	if err != nil {
		return err
	}

	for _, o := range os {
		if o == nil {
			continue
		}

		o.R.OrderItems = nil
	}

	for _, o := range os {
		if o == nil {
			continue
		}

		for _, rel := range orderItems {

			if !rel.DiscountID.IsValue() {
				continue
			}
			if !(rel.DiscountID.IsValue() && o.ID == rel.DiscountID.MustGet()) {
				continue
			}

			rel.R.Discount = o

			o.R.OrderItems = append(o.R.OrderItems, rel)
		}
	}

	return nil
}

// LoadOrders loads the discount's Orders into the .R struct
func (o *Discount) LoadOrders(ctx context.Context, exec bob.Executor, mods ...bob.Mod[*dialect.SelectQuery]) error {
	if o == nil {
		return nil
	}

	// Reset the relationship
	o.R.Orders = nil

	related, err := o.Orders(mods...).All(ctx, exec)
	if err != nil {
		return err
	}

	for _, rel := range related {
		rel.R.Discount = o
	}

	o.R.Orders = related
	return nil
}

// LoadOrders loads the discount's Orders into the .R struct
func (os DiscountSlice) LoadOrders(ctx context.Context, exec bob.Executor, mods ...bob.Mod[*dialect.SelectQuery]) error {
	if len(os) == 0 {
		return nil
	}

	orders, err := os.Orders(mods...).All(ctx, exec)
	if err != nil {
		return err
	}

	for _, o := range os {
		if o == nil {
			continue
		}

		o.R.Orders = nil
	}

	for _, o := range os {
		if o == nil {
			continue
		}

		for _, rel := range orders {

			if !rel.DiscountID.IsValue() {
				continue
			}
			if !(rel.DiscountID.IsValue() && o.ID == rel.DiscountID.MustGet()) {
				continue
			}

			rel.R.Discount = o

			o.R.Orders = append(o.R.Orders, rel)
		}
	}

	return nil
}

type discountJoins[Q dialect.Joinable] struct {
	typ        string
	OrderItems modAs[Q, orderItemColumns]
	Orders     modAs[Q, orderColumns]
}

func (j discountJoins[Q]) aliasedAs(alias string) discountJoins[Q] {
	return buildDiscountJoins[Q](buildDiscountColumns(alias), j.typ)
}

func buildDiscountJoins[Q dialect.Joinable](cols discountColumns, typ string) discountJoins[Q] {
	return discountJoins[Q]{
		typ: typ,
		OrderItems: modAs[Q, orderItemColumns]{
			c: OrderItems.Columns,
			f: func(to orderItemColumns) bob.Mod[Q] {
				mods := make(mods.QueryMods[Q], 0, 1)

				{
					mods = append(mods, dialect.Join[Q](typ, OrderItems.Name().As(to.Alias())).On(
						to.DiscountID.EQ(cols.ID),
					))
				}

				return mods
			},
		},
		Orders: modAs[Q, orderColumns]{
			c: Orders.Columns,
			f: func(to orderColumns) bob.Mod[Q] {
				mods := make(mods.QueryMods[Q], 0, 1)

				{
					mods = append(mods, dialect.Join[Q](typ, Orders.Name().As(to.Alias())).On(
						to.DiscountID.EQ(cols.ID),
					))
				}

				return mods
			},
		},
	}
}
