func (o *OrderItem) Preload(name string, retrieved any) error {
	if o == nil {
		return nil
	}

	switch name {
	case "Order":
		rel, ok := retrieved.(*Order)
		if !ok {
			return fmt.Errorf("orderItem cannot load %T as %q", retrieved, name)
		}

		o.R.Order = rel

		if rel != nil {
			rel.R.OrderItems = OrderItemSlice{o}
		}
		return nil
	case "Product":
		rel, ok := retrieved.(*Product)
		if !ok {
			return fmt.Errorf("orderItem cannot load %T as %q", retrieved, name)
		}

		o.R.Product = rel

		if rel != nil {
			rel.R.OrderItems = OrderItemSlice{o}
		}
		return nil
	case "Discount":
		rel, ok := retrieved.(*Discount)
		if !ok {
			return fmt.Errorf("orderItem cannot load %T as %q", retrieved, name)
		}

		o.R.Discount = rel

		if rel != nil {
			rel.R.OrderItems = OrderItemSlice{o}
		}
		return nil
	default:
		return fmt.Errorf("orderItem has no relationship %q", name)
	}
}

