// LoadOrder loads the orderItem's Order into the .R struct
func (o *OrderItem) LoadOrder(ctx context.Context, exec bob.Executor, mods ...bob.Mod[*dialect.SelectQuery]) error {
	if o == nil {
		return nil
	}

	// Reset the relationship
	o.R.Order = nil

	related, err := o.Order(mods...).One(ctx, exec)
	if err != nil {
		return err
	}

	related.R.OrderItems = OrderItemSlice{o}

	o.R.Order = related
	return nil
}

// LoadOrder loads the orderItem's Order into the .R struct
func (os OrderItemSlice) LoadOrder(ctx context.Context, exec bob.Executor, mods ...bob.Mod[*dialect.SelectQuery]) error {
	if len(os) == 0 {
		return nil
	}

	orders, err := os.Order(mods...).All(ctx, exec)
	if err != nil {
		return err
	}

	for _, o := range os {
		if o == nil {
			continue
		}

		for _, rel := range orders {

			if !(o.OrderID == rel.ID) {
				continue
			}

			rel.R.OrderItems = append(rel.R.OrderItems, o)

			o.R.Order = rel
			break
		}
	}

	return nil
}

