type orderItemJoins[Q dialect.Joinable] struct {
	typ      string
	Order    modAs[Q, orderColumns]
	Product  modAs[Q, productColumns]
	Discount modAs[Q, discountColumns]
}

func (j orderItemJoins[Q]) aliasedAs(alias string) orderItemJoins[Q] {
	return buildOrderItemJoins[Q](buildOrderItemColumns(alias), j.typ)
}

func buildOrderItemJoins[Q dialect.Joinable](cols orderItemColumns, typ string) orderItemJoins[Q] {
	return orderItemJoins[Q]{
		typ: typ,
		Order: modAs[Q, orderColumns]{
			c: Orders.Columns,
			f: func(to orderColumns) bob.Mod[Q] {
				mods := make(mods.QueryMods[Q], 0, 1)

				{
					mods = append(mods, dialect.Join[Q](typ, Orders.Name().As(to.Alias())).On(
						to.ID.EQ(cols.OrderID),
					))
				}

				return mods
			},
		},
		Product: modAs[Q, productColumns]{
			c: Products.Columns,
			f: func(to productColumns) bob.Mod[Q] {
				mods := make(mods.QueryMods[Q], 0, 1)

				{
					mods = append(mods, dialect.Join[Q](typ, Products.Name().As(to.Alias())).On(
						to.ID.EQ(cols.ProductID),
					))
				}

				return mods
			},
		},
		Discount: modAs[Q, discountColumns]{
			c: Discounts.Columns,
			f: func(to discountColumns) bob.Mod[Q] {
				mods := make(mods.QueryMods[Q], 0, 1)

				{
					mods = append(mods, dialect.Join[Q](typ, Discounts.Name().As(to.Alias())).On(
						to.ID.EQ(cols.DiscountID),
					))
				}

				return mods
			},
		},
	}
}
